mapper.select:
select * from
(select rownum as rnum, a.* from(
SELECT *
FROM singo
INNER JOIN member ON member.idx = singo.member_idx
WHERE singo.status = '활성' AND singo.member_idx = #{idx}
ORDER BY "CHECK" ASC
	)a )b
where rnum >=#{start} and rnum <=#{end}

select count(*)
from singo
where status='활성' and member_idx=#{idx}

mapper.update:
update member
set status='활성',singo_count=0
where idx=#{idx}

update singo
set status='비활성'
where idx=#{idx}
