mapper.select:
	        select * from(
        select  rownum as rnum, a.* from(
        select  *  from notice
       	where status='활성화'
        )a
        ) b
        where rnum>=#{start} and rnum<=#{end}

select * from(
select rownum as rnum, a.* from(
select * from notice
where status='대기'
)a
)b
where rnum>=#{start} and rnum<=#{end}

mapper.update:
update notice set
status = '활성'
where idx=${idx}

update notice set
status = '거부'
where idx=${idx}

update notice set
status = '비활성'
where idx=${idx}

mapper.select:
select * from(
select rownum as rnum, a.* from(
select * from notice
order by idx asc
)a
)b
where rnum>=#{start} and rnum<=#{end}

SELECT count(*)
FROM notice
WHERE status = '활성'
AND workday like
#{workday}

	AND (

		local2 IN

			#{item}



		OR


		local3 IN

			#{item}


	)


	AND ( job in

		#{item}

	)

select * from (select rownum as rnum,a. * from (
SELECT * FROM notice
WHERE status = '활성'
AND workday LIKE #{workday}

	AND (

		local2 IN

			#{item}



		OR


		local3 IN

			#{item}


	)


	AND ( job in

		#{item}

	)

ORDER BY ref desc)a)
where rnum >=#{start}
and rnum<=#{end}
