mapper.insert:
insert into plan_up
values(
plan_up_idx.nextval,
plan_idx_se.currval,
#{count}
)

mapper.update:
update plan set
status='비활성화'
where idx=#{idx}

update plan set
plan_name=#{plan_name},
plan_price=#{plan_price}
where idx=#{idx}

update plan_up set
count=#{count}
where plan_idx=#{idx}

mapper.select:
select * from plan,plan_up
where plan.idx=plan_up.plan_idx
and plan.idx=#{idx}
